// # the vertex shader is responsible for taking all of the data you provide about a piece of geometry ÑÊthe positions of the vertices, and aux information of the vertices, and any global variables and transforming the geometry.

// # any results from doing this get passed to the geometry shader (if you have one, they are pretty rare) and then ultimately the fragment shader

void main()
{
	// # this line is the simplest vertex shader posible ÑÊit just transforms the vertex as if it is being looked at by a normal perspective camera
	gl_Position = gl_ModelViewProjectionMatrix * gl_Vertex;
}

// # the fragment shader tells OpenGL what to draw in a pixel. OpenGL has turned the geometry that comes out of the vertex shader (or geometry shader) into a list of pixels that it has to draw.

void main()
{	
	// # this is the simplest fragment shader possible ÑÊit just says draw this pixel (this 'fragment') with a dark, opaque, grey
	gl_FragColor = vec4(0.1, 0.1, 0.1, 1);

	// # if you wanted red:
	//gl_FragColor = vec4(1,0,0,1);
}

varying vec4 vertexColor;

attribute vec4 s_Color;

uniform vec4 extraColor;

void main()
{
        gl_Position = gl_ModelViewProjectionMatrix * gl_Vertex;
        vertexColor = extraColor;
}

varying vec4 vertexColor;

void main()
{
        gl_FragColor = vertexColor;
}

