echo "BENVOLIO: Could not take truce with the unruly spleen"; say -v $BENVOLIO "Could not take truce with the unruly spleen"
echo "BENVOLIO: Of Tybalt deaf to peace, but that he tilts"; say -v $BENVOLIO "Of Tybalt deaf to peace, but that he tilts"
echo "BENVOLIO: With piercing steel at bold Mercutio's breast,"; say -v $BENVOLIO "With piercing steel at bold Mercutio's breast,"
echo "BENVOLIO: Who all as hot, turns deadly point to point,"; say -v $BENVOLIO "Who all as hot, turns deadly point to point,"
echo "BENVOLIO: And, with a martial scorn, with one hand beats"; say -v $BENVOLIO "And, with a martial scorn, with one hand beats"
echo "BENVOLIO: Cold death aside, and with the other sends"; say -v $BENVOLIO "Cold death aside, and with the other sends"
echo "BENVOLIO: It back to Tybalt, whose dexterity,"; say -v $BENVOLIO "It back to Tybalt, whose dexterity,"
