echo "BENVOLIO: his tongue,"; say -v $BENVOLIO "his tongue,"
echo "BENVOLIO: His agile arm beats down their fatal points,"; say -v $BENVOLIO "His agile arm beats down their fatal points,"
echo "BENVOLIO: And 'twixt them rushes; underneath whose arm"; say -v $BENVOLIO "And 'twixt them rushes; underneath whose arm"
echo "BENVOLIO: An envious thrust from Tybalt hit the life"; say -v $BENVOLIO "An envious thrust from Tybalt hit the life"
echo "BENVOLIO: Of stout Mercutio, and then Tybalt fled;"; say -v $BENVOLIO "Of stout Mercutio, and then Tybalt fled;"
echo "BENVOLIO: But by and by comes back to Romeo,"; say -v $BENVOLIO "But by and by comes back to Romeo,"
echo "BENVOLIO: Who had but newly entertain'd revenge,"; say -v $BENVOLIO "Who had but newly entertain'd revenge,"
echo "BENVOLIO: And to 't they go like lightning, for, ere I"; say -v $BENVOLIO "And to 't they go like lightning, for, ere I"
